// ============================================================================
// MERGE TRANSCRIPTS FROM ALL SOURCES
// Normalizes PDF, Audio (Whisper), and Text outputs to unified $json.data format
// ============================================================================

const input = $input.all();

// Determine transcript source and extract text
let transcriptText = '';
let transcriptSource = 'unknown';

for (const item of input) {
  // Whisper outputs to .text field
  if (item.json.text) {
    transcriptText = item.json.text;
    transcriptSource = 'whisper';
    break;
  }

  // PDF extraction outputs to .data field
  if (item.json.data && typeof item.json.data === 'string') {
    transcriptText = item.json.data;
    transcriptSource = item.json.source || 'pdf';
    break;
  }

  // JSON/VTT handled text also in .data
  if (item.json.data) {
    transcriptText = item.json.data;
    transcriptSource = 'text';
    break;
  }
}

// ============================================================================
// RETURN NORMALIZED TRANSCRIPT
// ============================================================================

return [{
  json: {
    ...input[0].json,  // Preserve all original metadata
    data: transcriptText,  // Normalized transcript field
    transcriptSource: transcriptSource,  // Track origin
    mergeDebug: {
      inputCount: input.length,
      source: transcriptSource,
      textLength: transcriptText.length,
      timestamp: new Date().toISOString()
    }
  }
}];
