// ============================================================================
// CHECK FILE TYPE (PDF / Audio / Text)
// Determines processing path based on file extension and MIME type
// PRESERVES BINARY DATA for downstream nodes
// ============================================================================

const input = $input.first();
const inputJson = input.json;
const inputBinary = input.binary;  // CRITICAL: Preserve binary data

const fileName = (inputJson.name || '').toLowerCase();
const mimeType = (inputJson.mimeType || '').toLowerCase();

// ============================================================================
// FILE TYPE DETECTION
// ============================================================================

// Audio file extensions
const audioExtensions = ['.mp3', '.m4a', '.wav', '.webm', '.ogg', '.flac', '.mpeg', '.mpga', '.mp4'];
const isAudio = audioExtensions.some(ext => fileName.endsWith(ext)) || mimeType.includes('audio/');

// PDF detection
const isPDF = fileName.endsWith('.pdf') || mimeType.includes('pdf');

// Text file extensions
const textExtensions = ['.txt', '.json', '.vtt'];
const isText = textExtensions.some(ext => fileName.endsWith(ext)) || mimeType.includes('text/');

// Determine file type
let fileType = 'unknown';
if (isAudio) {
  fileType = 'audio';
} else if (isPDF) {
  fileType = 'pdf';
} else if (isText) {
  fileType = 'text';
}

// ============================================================================
// RETURN FILE TYPE METADATA + BINARY DATA
// ============================================================================

return [{
  json: {
    ...inputJson,
    fileType: fileType,
    isAudio: isAudio,
    isPDF: isPDF,
    isText: isText,
    detectionDebug: {
      fileName: fileName,
      mimeType: mimeType,
      detectedType: fileType,
      hasBinaryData: !!inputBinary,  // Track if binary data present
      timestamp: new Date().toISOString()
    }
  },
  binary: inputBinary  // CRITICAL: Pass through binary data
}];

// Handle JSON/VTT - Normalize transcript data to $json.data
// ENHANCED: Preserve meeting date metadata from Merge Meeting Date node
const input = $input.first().json;

// First, get the raw input from various possible fields
let rawData = input.data || input.text || input.content || '';
const mimeType = input.mimeType || '';
let extractedText = rawData;
let metadata = {};

// Check if this is a JSON file
if (mimeType === 'application/json' || (typeof rawData === 'string' && rawData.startsWith('{'))) {
  try {
    const jsonData = JSON.parse(rawData);
    // Look for transcript in common fields
    if (jsonData.transcript) {
      extractedText = jsonData.transcript;
    } else if (jsonData.text) {
      extractedText = jsonData.text;
    } else if (jsonData.content) {
      extractedText = jsonData.content;
    } else if (jsonData.transcription) {
      extractedText = jsonData.transcription;
    } else if (jsonData.segments) {
      // Whisper API format
      extractedText = jsonData.segments.map(s => s.text).join(' ');
    }
    metadata = jsonData.metadata || {};
  } catch (e) {
    // Not JSON, keep as is
  }
}

// Check for VTT format
if (mimeType === 'text/vtt' || (typeof extractedText === 'string' && extractedText.includes('WEBVTT'))) {
  const lines = extractedText.split('\n');
  let transcript = '';
  for (const line of lines) {
    if (!line.includes('-->') && !line.match(/^\d+$/) && !line.startsWith('WEBVTT') && line.trim()) {
      transcript += line.replace(/<[^>]*>/g, '').trim() + ' ';
    }
  }
  extractedText = transcript.trim();
  metadata.format = 'vtt';
}

// Return normalized format with meeting date metadata preserved
return [{
  json: {
    data: extractedText,
    metadata: metadata,
    // Preserve meeting date fields from Merge Meeting Date node
    meetingDate: input.meetingDate,
    meetingDateFormatted: input.meetingDateFormatted,
    meetingDateShort: input.meetingDateShort,
    meetingDateSource: input.meetingDateSource
  }
}];

// ============================================================================
// HEALTHCARE METADATA CLASSIFIER
// Detects healthcare specialty and extracts metadata for patient summaries
// ============================================================================

const input = $input.first().json;
const transcript = input.data || '';
const fileName = $('Process Webhook Payload').item.json.name || '';

// ============================================================================
// HEALTHCARE KEYWORDS (Validation)
// ============================================================================

const healthcareKeywords = [
  'patient', 'doctor', 'physician', 'dr.', 'dr ', 'nurse',
  'diagnosis', 'treatment', 'symptoms', 'medication',
  'dosage', 'prescription', 'exam', 'test results',
  'lab work', 'hospital', 'clinic', 'office visit',
  'psa', 'blood pressure', 'heart rate', 'cholesterol',
  'ultrasound', 'surgery', 'post-surgery', 'follow-up',
  'medical', 'clinical', 'health', 'provider',
  'urologist', 'cardiologist', 'oncologist', 'therapist'
];

// ============================================================================
// HEALTHCARE VALIDATION
// ============================================================================

const lowerTranscript = transcript.toLowerCase();
const lowerFileName = fileName.toLowerCase();
const combinedText = lowerTranscript + ' ' + lowerFileName;

// Count healthcare keyword matches
let healthcareScore = 0;

for (const keyword of healthcareKeywords) {
  const regex = new RegExp('\\b' + keyword + '\\b', 'g');
  const matches = combinedText.match(regex);
  if (matches) {
    healthcareScore += matches.length;
  }
}

// Validate this is actually a healthcare transcript
if (healthcareScore < 3) {
  throw new Error(`Transcript does not appear to be from a healthcare appointment (score: ${healthcareScore}).

Healthcare keywords found: ${healthcareScore}
Minimum required: 3

Please verify:
- This is a medical/dental/therapy appointment transcript
- The file contains actual healthcare conversation content
- The file is not empty or corrupted

If this is a healthcare transcript, please contact support.`);
}

// ============================================================================
// SPECIALTY DETECTION (Multi-Specialty Foundation)
// ============================================================================

const specialtyPatterns = {
  'dental': ['dentist', 'dental', 'tooth', 'teeth', 'cavity', 'filling', 'crown', 'root canal', 'gums', 'cleaning', 'orthodont'],
  'physical-therapy': ['physical therapist', 'PT ', ' pt ', 'range of motion', 'ROM', 'exercises', 'strengthening', 'stretching', 'mobility', 'rehab'],
  'mental-health': ['therapist', 'psychologist', 'psychiatrist', 'counselor', 'anxiety', 'depression', 'therapy session', 'coping', 'mental health', 'behavioral'],
  'urology': ['urologist', 'prostate', 'PSA', 'bladder', 'kidney', 'urinary', 'urine'],
  'cardiology': ['cardiologist', 'heart', 'cardiac', 'blood pressure', 'EKG', 'ECG', 'echocardiogram', 'cholesterol', 'cardiovascular'],
  'oncology': ['oncologist', 'cancer', 'tumor', 'chemotherapy', 'chemo', 'radiation', 'metastasis', 'biopsy', 'malignant'],
  'endocrinology': ['endocrinologist', 'diabetes', 'diabetic', 'thyroid', 'A1C', 'glucose', 'insulin', 'blood sugar', 'hormone'],
  'primary-care': ['primary care', 'family doctor', 'general practitioner', 'annual physical', 'checkup', 'wellness visit', 'PCP'],
  'dermatology': ['dermatologist', 'skin', 'rash', 'mole', 'melanoma', 'acne', 'dermatology', 'eczema', 'psoriasis'],
  'orthopedics': ['orthopedic', 'orthopedist', 'bone', 'joint', 'fracture', 'arthritis', 'knee', 'hip', 'shoulder', 'spine']
};

// Score each specialty
const specialtyScores = {};

for (const [specialty, keywords] of Object.entries(specialtyPatterns)) {
  let score = 0;
  for (const keyword of keywords) {
    const regex = new RegExp('\\b' + keyword.toLowerCase() + '\\b', 'g');
    const matches = combinedText.match(regex);
    if (matches) {
      score += matches.length;
    }
  }
  specialtyScores[specialty] = score;
}

// Find highest scoring specialty
let detectedSpecialty = 'general-medical';  // Default
let maxSpecialtyScore = 0;

for (const [specialty, score] of Object.entries(specialtyScores)) {
  if (score > maxSpecialtyScore && score >= 2) {  // Require at least 2 keyword matches
    maxSpecialtyScore = score;
    detectedSpecialty = specialty;
  }
}

// ============================================================================
// PROVIDER DETECTION (Enhanced with Role)
// ============================================================================

const providerMatch = transcript.match(/Dr\.?\s+([A-Z][a-z]+)/i);
let providerName = 'Your healthcare provider';
let providerRole = detectedSpecialty;  // Infer role from detected specialty

if (providerMatch) {
  providerName = `Dr. ${providerMatch[1]}`;

  // Try to detect provider role from context around their name
  const contextRegex = new RegExp(`(\\w+)\\s+Dr\\.?\\s+${providerMatch[1]}`, 'i');
  const roleMatch = transcript.match(contextRegex);

  if (roleMatch) {
    const potentialRole = roleMatch[1].toLowerCase();
    // Map common titles to specialties
    const titleMap = {
      'urologist': 'urology',
      'cardiologist': 'cardiology',
      'dentist': 'dental',
      'therapist': 'physical-therapy',
      'psychologist': 'mental-health',
      'psychiatrist': 'mental-health',
      'oncologist': 'oncology',
      'endocrinologist': 'endocrinology',
      'dermatologist': 'dermatology',
      'orthopedist': 'orthopedics',
      'orthopedic': 'orthopedics'
    };
    providerRole = titleMap[potentialRole] || detectedSpecialty;
  }
}

// ============================================================================
// CLINICAL CONTEXT DETECTION
// ============================================================================

let appointmentType = 'follow-up';  // Default

if (/\b(diagnosis|diagnosing|diagnosed|new diagnosis)\b/i.test(transcript)) {
  appointmentType = 'diagnosis';
} else if (/\b(treatment|treating|therapy|starting treatment)\b/i.test(transcript)) {
  appointmentType = 'treatment-planning';
} else if (/\b(consultation|consult|consulting|initial visit|first visit)\b/i.test(transcript)) {
  appointmentType = 'consultation';
} else if (/\b(follow.?up|monitoring|check.?up|routine visit)\b/i.test(transcript)) {
  appointmentType = 'follow-up-monitoring';
} else if (/\b(post.?op|after surgery|post.?surgery|surgical follow.?up)\b/i.test(transcript)) {
  appointmentType = 'post-operative';
}

// ============================================================================
// PATIENT PRESENCE DETECTION
// ============================================================================

const patientPresent = /\b(I am|I'm|my|me|myself)\b/i.test(transcript);

// ============================================================================
// SENSITIVE DATA FLAGS
// ============================================================================

const sensitiveDataFlags = {
  phi: true,  // Healthcare data is always PHI
  pii: /\b(\d{3}-\d{2}-\d{4}|\d{9})\b/.test(transcript),  // SSN pattern
  financialData: /\b(credit card|account number|routing number|card number)\b/i.test(transcript)
};

// Log warning if sensitive data detected
if (sensitiveDataFlags.pii || sensitiveDataFlags.financialData) {
  console.warn('âš ï¸  SENSITIVE DATA DETECTED:', {
    fileName: fileName,
    pii: sensitiveDataFlags.pii,
    financialData: sensitiveDataFlags.financialData,
    timestamp: new Date().toISOString()
  });
}

// ============================================================================
// RETURN ENRICHED DATA
// ============================================================================

return [{
  json: {
    ...input,
    healthcareScore: healthcareScore,
    healthcareMetadata: {
      specialty: detectedSpecialty,
      specialtyScore: maxSpecialtyScore,
      appointmentType: appointmentType,
      provider: {
        name: providerName,
        role: providerRole,
        isPrimary: true
      },
      patientPresent: patientPresent
    },
    sensitiveDataFlags: sensitiveDataFlags
  }
}];

// ============================================================================
// CONSTRUCT HEALTHCARE GAMMA PAYLOAD
// Healthcare-specific theme selection + Gamma API v1.0 payload construction
// ============================================================================

const input = $input.first().json;
const analysisText = input.text || input.output || '';
const meetingDate = $('Process Webhook Payload').item.json.meetingDateFormatted;
const fileName = $('Process Webhook Payload').item.json.name;

// ============================================================================
// GAMMA THEME IDS (Healthcare themes)
// ============================================================================

const THEME_IDS = {
  pearl: 'pearl',      // Clinical, professional
  sage: 'sage',        // Organic, caring, wellness
  consultant: 'consultant'  // Trustworthy, analytical
};

// ============================================================================
// HEALTHCARE THEME SELECTION (keyword scoring)
// ============================================================================

const healthcareKeywords = {
  clinical: {
    keywords: ['diagnosis', 'treatment', 'surgery', 'procedure', 'test results', 'clinical'],
    theme: 'pearl',
    weight: 2
  },
  wellness: {
    keywords: ['wellness', 'preventive', 'nutrition', 'lifestyle', 'holistic', 'natural'],
    theme: 'sage',
    weight: 2
  },
  chronic: {
    keywords: ['monitoring', 'followup', 'follow-up', 'chronic', 'management', 'ongoing'],
    theme: 'consultant',
    weight: 3
  }
};

// Score each theme
const themeScores = {};
const lowerText = analysisText.toLowerCase();

for (const [category, config] of Object.entries(healthcareKeywords)) {
  let score = 0;
  for (const keyword of config.keywords) {
    const regex = new RegExp('\\b' + keyword + '\\b', 'gi');
    const matches = lowerText.match(regex);
    if (matches) {
      score += matches.length * config.weight;
    }
  }
  themeScores[config.theme] = (themeScores[config.theme] || 0) + score;
}

// Select theme with highest score (default to consultant if tie)
let selectedTheme = 'consultant';
let maxScore = 0;

for (const [theme, score] of Object.entries(themeScores)) {
  if (score > maxScore) {
    maxScore = score;
    selectedTheme = theme;
  }
}

// ============================================================================
// IMAGE STYLE BASED ON THEME
// ============================================================================

const imageStyles = {
  pearl: 'clean, medical, professional, clinical photography',
  sage: 'natural, organic, wellness, calming earth tones',
  consultant: 'professional, trustworthy, healthcare, modern medical'
};

// ============================================================================
// CONSTRUCT GAMMA PAYLOAD
// ============================================================================

const gammaPayload = {
  inputText: analysisText,
  textMode: 'preserve',  // Don't rewrite patient language
  format: 'presentation',
  themeId: THEME_IDS[selectedTheme],
  numCards: 7,  // Fixed 7 slides
  cardSplit: 'inputTextBreaks',  // Respect --- breaks

  additionalInstructions: [
    'Preserve medical terminology with plain-language explanations.',
    'Use patient-friendly visual layouts for health data (tables, timelines, status indicators).',
    'Display meeting date prominently on title slide.',
    'Include "Confidential Medical Record" footer on all slides except title.',
    'Emphasize action items and follow-up requirements with clear visual hierarchy.',
    'Use visual health indicators (green=safe, yellow=watch, red=urgent) where appropriate.',
    'Maintain reassuring professional medical tone throughout.',
    'Preserve all specific numbers, dates, and medication dosages exactly as written.'
  ].join(' '),

  exportAs: 'pdf',

  textOptions: {
    amount: 'detailed',
    tone: 'professional medical, warm, educational, reassuring',
    audience: 'patient and family members',
    language: 'en'
  },

  imageOptions: {
    source: 'aiGenerated',
    model: 'gemini-2.5-flash-image',
    style: imageStyles[selectedTheme]
  },

  cardOptions: {
    dimensions: '16x9',
    headerFooter: {
      bottomLeft: {
        type: 'text',
        value: `${meetingDate} | Confidential Medical Record`,
        size: 'sm'
      },
      bottomRight: {
        type: 'cardNumber'
      },
      hideFromFirstCard: true
    }
  },

  sharingOptions: {
    workspaceAccess: 'edit',
    externalAccess: 'view'
  }
};

// ============================================================================
// RETURN PAYLOAD + METADATA
// ============================================================================

return [{
  json: {
    output: gammaPayload,
    metadata: {
      selectedTheme: selectedTheme,
      themeScores: themeScores,
      meetingDate: meetingDate,
      fileName: fileName,
      analysisWordCount: analysisText.split(/\s+/).length,
      timestamp: new Date().toISOString()
    }
  }
}];

// Healthcare Presentation Retry Logic
const input = $input.first().json;
const maxRetries = 20;  // 20 retries Ã— 15s = 5 min max
const retryCount = input.retryCount || 0;

if (retryCount >= maxRetries) {
  throw new Error(`Healthcare presentation generation timed out after ${maxRetries} retries (${maxRetries * 15} seconds)`);
}

return [{
  json: {
    ...input,
    retryCount: retryCount + 1
  }
}];

