// ============================================================================
// NORMALIZE ASSEMBLYAI OUTPUT
// Converts AssemblyAI response to format expected by downstream nodes
// ============================================================================

const input = $input.first().json;

const transcriptText = input.text || '';

if (!transcriptText || transcriptText.trim().length === 0) {
  throw new Error('No transcript text found in AssemblyAI response');
}

const medications = (input.entities || []).filter(e => e.entity_type === 'drug');
const conditions = (input.entities || []).filter(e => e.entity_type === 'medical_condition');
const procedures = (input.entities || []).filter(e => e.entity_type === 'medical_process');

return [{
  json: {
    text: transcriptText,
    data: transcriptText,

    assemblyaiMetadata: {
      transcriptId: input.id,
      confidence: input.confidence,
      audioDuration: input.audio_duration,
      medications: medications,
      conditions: conditions,
      procedures: procedures,
      speakerCount: input.speaker_labels?.speakers || 0,
      speakerSegments: input.speaker_labels?.segments || []
    },

    meetingDate: $('Process Webhook Payload').item.json.meetingDate,
    meetingDateFormatted: $('Process Webhook Payload').item.json.meetingDateFormatted,
    meetingDateShort: $('Process Webhook Payload').item.json.meetingDateShort
  }
}];

const input = $input.first();
const body = input.json.body || input.json || {};
const inputBinary = input.binary || {};

const appointmentDate = body.appointmentDate || '';
const providerName = body.providerName || '';
const specialty = body.specialty || '';
const recipientEmail = body.recipientEmail || '';
const fileFormat = body.fileFormat || 'm4a';

let meetingDate = DateTime.now();
let meetingDateSource = 'fallback';

if (appointmentDate) {
  const parsed = DateTime.fromISO(appointmentDate);
  if (parsed.isValid) {
    meetingDate = parsed;
    meetingDateSource = 'webhook';
  }
}

const mimeTypes = {
  'm4a': 'audio/m4a',
  'mp3': 'audio/mpeg',
  'wav': 'audio/wav',
  'webm': 'audio/webm',
  'ogg': 'audio/ogg',
  'flac': 'audio/flac',
  'pdf': 'application/pdf',
  'txt': 'text/plain',
  'json': 'application/json'
};

const mimeType = mimeTypes[fileFormat] || `audio/${fileFormat}`;
const fileName = inputBinary.data0?.fileName || `recording.${fileFormat}`;

return [{
  json: {
    appointmentDate: meetingDate.toISODate(),
    meetingDate: meetingDate.toISODate(),
    meetingDateFormatted: meetingDate.toFormat('MMMM d, yyyy'),
    meetingDateShort: meetingDate.toFormat('MMM d, yyyy'),
    meetingDateSource: meetingDateSource,
    providerName: providerName,
    specialty: specialty,
    recipientEmail: recipientEmail,
    fileFormat: fileFormat,
    name: fileName,
    mimeType: mimeType
  },
  binary: {
    data: inputBinary.data0
  }
}];
